import Faq from "../models/Fag.model";

const lists = [
  {
    questions: "Do you have any insurance",
    answers:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Tenetur, mollitia?",
  },
  {
    questions: "How to be safe buying online",
